def parse_json(json_str):
    msg_list = json.loads(json_str)['list']
    for msg in msg_list:
        app_msg_ext_info = msg['app_msg_ext_info']
        title, content_url, source_url = get_info(app_msg_ext_info)
        print(title + ", " + content_url + ", " + source_url)
        is_multi = app_msg_ext_info['is_multi']
        if is_multi:
            multi_app_msg_item_list = app_msg_ext_info['multi_app_msg_item_list']
            for multi_msg in multi_app_msg_item_list:
                title, content_url, source_url = get_info(multi_msg)
                print(title + ", " + content_url + ", " + source_url)


def get_info(msg_dict):
    title = msg_dict['title']
    content_url = parser.unescape(msg_dict['content_url']).replace("\\", "")
    source_url = parser.unescape(msg_dict['source_url']).replace("\\", "")
    return title, content_url, source_url


